componentDidMount(){
  // I will use fake api from jsonplaceholder website
  // this return 100 posts 
  fetch('/recommend')
  .then( response => response.json())
  .then(
      // handle the result
      (customers) => {
          console.log(customers)
          var customer;
          if(!customers){
