import React, { Component } from 'react';
import Axios from 'axios';
import CardList from '../../../CardList/CardList'
// import axios;
class Automate extends Component {
  constructor(props){
    super(props);
    this.state={
    result:[]
  }
  }
  componentDidMount(){
    Axios.get('https://smart-flask-api.herokuapp.com/ml/automate')
      .then(res=>{
        console.log(res.data)
        const result = res.data;
        this.setState({result:result})
      })
  }
  additem(name,quantity){
    console.log(name.value,quantity.value)
    // this.state.result.push({"item_id":"item"+this.state.result.length,"item_name":name,"quantity":quantity})
    this.setState(state => {
      const list = state.result.push(state.value);
      return {
        list,
        value: {"item_id":"item"+this.state.result.length,"item_name":name,"quantity":quantity},
      };
  })
}
  render() {
    return (
      <div>
        <h1>Automate</h1>
        {/* <form action="https://smart-flask-api.herokuapp.com/ml/automate" method="GET">
          <input name="itemid" placeholder="item id"></input>
          <input name="" id="" class="btn btn-primary" type="submit" value="Add"></input>
        </form> */}
        {/* <ul>
          {this.state.result.map(element=>
            <li key={element.key}>{element.key}</li>
          )}
        </ul> */}
        <form >
            <input name="item_name" id="itemname" placeholder="add item name"></input>
            <input name="quantity" id="quantity" placeholder="quantity"></input>
            <a onClick={this.additem.bind(document.getElementsById("itemname").value,document.getElementById("quantity").value,this)}>Add</a>
        </form>
        <CardList/>

      </div>
    );
  }
}

export default Automate;
