import React, { Component } from 'react';
import Axios from 'axios';
// import axios;
class Recommend extends Component {
  state={
    result:[]
  }
  componentDidMount(){
    Axios.get('https://smart-flask-api.herokuapp.com/ml/recommend')
      .then(res=>{
        console.log(res.data)
        const result = res.data;
        this.setState({result:result})
      })
  }
  render() {
    // const {reslut} = this.props;

        let travelTo = this.state.result.travelTo || 0, className = 'card';

        if (travelTo !== 0) {
            className += ' traveling';
        }
    return (
      <div>
      <div class="container-fluid">
        <h1>Recommend</h1>
        </div> 
        <div class="container-fluid">
        <div className="cards-list">
        <ul>
          {this.state.result.map(element=>
          <div className={className}
          style={{opacity: this.state.result.removing ? 0 : 1, transform:`translate3d(0,${travelTo}px,0)`}}
          id={this.state.result.key}>
           <div className='card-inner ripple'>
               <div className="information" key={this.state.result.item_id}>
                   <div className="value">{this.state.result.key}</div>
                   <div className="value">{this.state.result.item_id}</div>
                   <div className="value">{this.state.result.item_name}</div>
                   <button onclick="handleClick">Tick</button>

                   {/* <img src={card.imageSrc} className='card-image' alt='some alt'/>
                   <div className="value">{card.value}</div>
                   <div className="label">{card.label}</div> */}
               </div>
           </div>
           </div>
          )}
        </ul>
        </div>
        </div>
      </div>

    );
  }
}

export default Recommend;
