componentDidMount(){
  // I will use fake api from jsonplaceholder website
  // this return 100 posts 
  fetch('http://127.0.0.1:5000/recommend')
  .then( response => response.json())
  .then(
      // handle the result
      (data) => {
         // console.log(customers)
          var customer;
